import loggingfrom chatterbot import ChatBotfrom chatterbot.trainers import ChatterBotCorpusTrainerfrom chatterbot.trainers import ListTrainerfrom flask import Flask, render_template, requestfrom sqlalchemy import create_engineapp = Flask(__name__)logger = logging.getLogger()logger.setLevel(logging.CRITICAL)bot = ChatBot('River', storage_adapter='chatterbot.storage.SQLStorageAdapter')engine=create_engine('sqlite:///data.db', echo=True, connect_args={"check_same_thread": False})trainer = ListTrainer(bot)trainer.train(['What is your name?', 'My name is River I am an artificial intelligence robot'])trainer.train(['How are you?', 'I am great as I was born yesterday' ])trainer.train(['Bye?', 'Bye, see you later' ])trainer.train(['Who created you, Rayyan The Great'])conversation = [    "Hello",    "Hello!!",    "How are you doing?",    "I'm doing great.",    "That is good to hear",    "Thank you.",    "You're welcome."]trainer.train(conversation)corpus_trainer = ChatterBotCorpusTrainer(bot)corpus_trainer.train('chatterbot.corpus.english')@app.route('/')def chat():    return render_template('index.html')@app.route('/get')def get_bot_response():    userText = request.args.get('msg')    return str(bot.get_response(userText))if __name__ == '__main__':    app.run()